import * as React from 'react'
import { PropertyControls, ControlType } from 'framer'

// Define type of property
interface Props {
  url: string
  newTab: boolean
}

export class HTML_A extends React.Component<Props> {
  // Set default properties
  static defaultProps = {
    url: '#',
    newTab: false,
    children: null,
    width: 50,
    height: 50,
  }

  // Items shown in property panel
  static propertyControls: PropertyControls = {
    url: { type: ControlType.String, title: 'URL' },
    newTab: { type: ControlType.Boolean, title: 'New Tab' },
  }

  render() {
    return (
      <a href={this.props.url} target={this.props.newTab ? '_blank' : '_self'}>
        {this.props.children}
      </a>
    )
  }
}
