import { Data, animate, Override, Animatable } from 'framer'

const data = Data({ scale: Animatable(1) })

export const Scale: Override = () => {
  return {
    scale: data.scale,
    onTap() {
      data.scale.set(0.6)
      animate.spring(data.scale, 1)
    },
  }
}

export const Fixed: Override = () => {
  return {
    style: {
      position: 'fixed',
      top: '0',
      zIndex: '99',
      transform: 'none',
    },
    scale: data.scale,
    onTap() {
      console.log('onTap')
      data.scale.set(0.6)
      animate.spring(data.scale, 1)
    },
  }
}
